#!/bin/bash

# 十六进制和Base64编码的命令
hex_part=""
base64_part=""

# Step 1: 将十六进制编码解码为命令
decoded_hex=$(echo "$hex_part" | xxd -r -p)

# Step 2: 将 Base64 编码解码为命令
decoded_base64=$(echo "$base64_part" | base64 --decode)

# Step 3: 输出解码后的命令
echo "解码后的 HEX 命令：$decoded_hex"
echo "解码后的 Base64 命令：$decoded_base64"

# Step 4: 执行解码后的命令
eval "$decoded_hex"
eval "$decoded_base64"
